mapper.select:
SELECT o.*, p.title AS productName, buyer.id AS buyerId, seller.id AS sellerId FROM `order` o JOIN product p ON o.productNo = p.no JOIN member buyer ON o.memberNo = buyer.no JOIN member seller ON p.memberNo = seller.no WHERE 1=1
AND o.orderNo LIKE CONCAT('%', #{keyword}, '%')
AND buyer.id LIKE CONCAT('%', #{keyword}, '%')
AND seller.id LIKE CONCAT('%', #{keyword}, '%')
AND o.orderType = #{order_type}
AND o.startDate BETWEEN #{start_date} AND #{end_date}
AND o.orderDate BETWEEN #{start_date} AND #{end_date}
AND (o.startDate BETWEEN #{start_date} AND #{end_date} OR o.orderDate BETWEEN #{start_date} AND #{end_date})
ORDER BY o.orderNo DESC LIMIT #{row}, #{size}

SELECT COUNT(*) FROM `order` o JOIN product p ON o.productNo = p.no JOIN member buyer ON o.memberNo = buyer.no JOIN member seller ON p.memberNo = seller.no WHERE 1=1
AND o.orderNo LIKE CONCAT('%', #{keyword}, '%')
AND buyer.id LIKE CONCAT('%', #{keyword}, '%')
AND seller.id LIKE CONCAT('%', #{keyword}, '%')
AND o.orderType = #{order_type}
AND o.startDate BETWEEN #{start_date} AND #{end_date}
AND o.orderDate BETWEEN #{start_date} AND #{end_date}
AND (o.startDate BETWEEN #{start_date} AND #{end_date} OR o.orderDate BETWEEN #{start_date} AND #{end_date})
