mapper.select:
SELECT *
  FROM product
ORDER BY ${orderBy}
LIMIT #{offset}, #{limit}

SELECT no, title, img, salePrice, rentPrice, secPrice, tradeType, createDate FROM product
AND title LIKE CONCAT('%', #{searchText}, '%')
AND categoryNo = #{categoryNo}
AND tradeType = #{tradeType}
ORDER BY view_cnt DESC
ORDER BY COALESCE(salePrice, rentPrice) ASC
ORDER BY COALESCE(salePrice, rentPrice) DESC
ORDER BY createDate DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM product
WHERE title LIKE CONCAT('%', #{name}, '%')

	AND tradeType = #{tradeType}

SELECT COUNT(*) FROM product
WHERE categoryNo = #{categoryNo}

	AND tradeType = #{tradeType}
