mapper.select:
SELECT no, title, img1, salePrice, rentPrice, secPrice, tradeType, createDate, latitude, longitude, deliveryAddr FROM product
isHide = 1 AND deleted = 'N'
AND title LIKE CONCAT('%', #{searchText}, '%')
AND categoryNo = #{categoryNo}
AND tradeType = #{tradeType}
AND isHide = #{isHide}
AND deleted = #{deleted}
ORDER BY CASE WHEN tradeType = '판매' THEN 1 WHEN tradeType = '대여' THEN 2 WHEN tradeType = '판매/대여' THEN 3 WHEN tradeType = '나눔' THEN 4 ELSE 5 END ASC, COALESCE(salePrice, rentPrice) ASC
ORDER BY CASE WHEN tradeType = '판매' THEN 1 WHEN tradeType = '대여' THEN 2 WHEN tradeType = '판매/대여' THEN 3 WHEN tradeType = '나눔' THEN 4 ELSE 5 END ASC, COALESCE(salePrice, rentPrice) DESC
ORDER BY createDate DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM product
isHide = 1 AND deleted = 'N'
AND tradeType = #{tradeType}

SELECT COUNT(*) FROM product
WHERE isHide = 1
  AND deleted = 'N'
  AND categoryNo = #{categoryNo}

  AND tradeType = #{tradeType}

mapper.update:
UPDATE product
SET view_cnt = view_cnt + 1
WHERE no = #{no}
