mapper.select:
SELECT gradeCount
FROM grade
WHERE gradeNo = #{gradeNo}

mapper.update:
UPDATE member
SET gradeId = #{gradeNo}
WHERE no = #{memberNo}

UPDATE settlement
SET finalSettleAmount = #{finalAmount}
WHERE settlementNo = #{settlementNo}

mapper.insert:
INSERT INTO settlement (
    orderNo, memberNo, productNo, revenueType, 
    feeAmount, finalSettleAmount, feeStatus
) VALUES (
    #{orderNo}, #{memberNo}, #{productNo}, #{revenueType},
    #{feeAmount}, #{finalSettleAmount},
    #{feeStatus}
)

mapper.select:
SELECT s.settlementNo, s.orderNo, p.memberNo AS sellerMemberNo, o.memberNo AS buyerMemberNo, s.productNo, p.title as productTitle, o.price, o.deliveryPrice, o.secPrice, g.gradeRate, s.feeAmount, s.finalSettleAmount, s.revenueType, s.settlementCompletedAt, o.orderDate AS payTime, s.feeStatus FROM settlement s LEFT JOIN `order` o ON s.orderNo = o.orderNo LEFT JOIN `product` p ON o.productNo = p.no LEFT JOIN member m ON p.memberNo = m.no LEFT JOIN grade g ON m.gradeId = g.gradeNo WHERE 1=1
AND s.revenueType = #{searchRevenueType}
AND s.feeStatus = #{searchFeeStatus}
AND s.settlementCompletedAt >= #{completedStart}
AND s.settlementCompletedAt <= #{completedEnd}
ORDER BY o.orderDate DESC

SELECT gradeRate
FROM grade
WHERE gradeNo = #{gradeId}

mapper.update:
UPDATE settlement
SET settlementCompletedAt = NOW()
WHERE settlementNo = #{settlementNo}

UPDATE settlement
SET feeStatus = 'COMPLETE'
WHERE settlementNo = #{settlementNo}

mapper.select:
SELECT DATE_FORMAT(settlementCompletedAt, '%Y-%m-%d %H:%i:%s')
FROM settlement
WHERE settlementNo = #{settlementNo}
