mapper.update:
UPDATE settlement
SET feeStatus = 'COMPLETE'
WHERE settlementNo = #{settlementNo}

UPDATE member
SET settlementCount = settlementCount + 1
WHERE memberNo = #{memberNo}

mapper.select:
SELECT memberNo, gradeNo, settlementCount
FROM member
WHERE memberNo = #{memberNo}

SELECT gradeCount
FROM grade
WHERE gradeNo = #{gradeNo}

mapper.update:
UPDATE member
SET gradeNo = #{gradeNo}
WHERE memberNo = #{memberNo}

UPDATE settlement
SET finalSettleAmount = #{finalAmount}
WHERE settlementNo = #{settlementNo}
